paper.project.layers[0].removeChildren();
var r = Math.seedrandom("Anne Paque");

for (var i = 0, l = 204;  i < l;  i++) {
  var x_off = Math.sin( i * 1 ) * x_wriggle, y_off = Math.cos( i * 1 ) * y_wriggle,
      x = 44 +  (i % 12)  * 48 + x_off, y = 44 + mod(i, 12) *  4 + y_off,
      b = new Path.Circle( adjust([ x, y ]), adjust(4) );
  b.fillColor = '#ffffff';
  if (color_spread > 0) {
    b.fillColor = '#00f000';
    b.fillColor.hue = i * color_spread;
//b.fillColor = new GradientColor( new Gradient(['#f40','#0fd']), scale([x+2,y+2]), scale([x-5,y-5]));
  } else {
    b.fillColor.saturation = 0.3;
    b.fillColor.hue = 90;
    b.fillColor.brightness = 0.9;
  }
  b.scale( x_stretch, y_stretch);
  b.rotate( rotate_initial + i * rotate_rate )//* r()*2);
  //if ((i % 8) == 0)
  b.position.x += (r() - 0.5) * messiness;
  b.position.y += (r() - 0.5) * messiness;
}
