functions.canvas:
return;
context.fillStyle="#111";
context.font = "Bold " + int(font_size) + "px Helvetica";
context.globalCompositeOperation = "xor";

var lines = [ 'Breton&', 'Breton&', "Breton&", "Breton." ],
    kern  = [ 0, .33, .34, .386, .365, .395, .42, .42, .35, .356 ],
    x_off = 0, y_off = 0;

$(lines).each(function(line_n, text) {  
  $(text.split('')).each( function(letter_n, letter) {
    context.fillText(
      letter,
      x_off + (font_size * (letter_n + 1) * kern[letter_n]), 
      y_off + (line_n + 1) * line_height
    );
  });
});

