paper.project.layers[0].removeChildren();
var center = [ scale(view.size.width / 2), scale(view.size.height / 2) ];

Math.seedrandom('Stuart Robinson');
//Math.seedrandom('Stuart Robinson');
var slices = 4 + Math.floor( Math.random() * 12 ),
    color_bands = [];

for (var i = 0; i < slices; i++) { color_bands[i] = Math.random() * 360; }

for (var i = n_rings, l = 0; i > l; i--) {
  var black = i % 2,
      c = new Path.Circle( center, scale(1 * black + Math.sqrt(i) * yeah/1) );

  var hue = color_bands[ Math.floor( i/(n_rings/slices) ) ] ;
  c.fillColor = '#f0f';
  //c.fillColor.hue = color_offset - (i * color_range);
  c.fillColor.hue = hue;
  c.fillColor.brightness = (i / 12);
  c.scale( x_scale, y_scale );
//  c.position.x += scale( Math.random() * 3); c.position.y += scale( Math.random() * 3);
  if (black) {
    c.fillColor = 'black';
  }
}
