paper.project.layers[0].removeChildren();
var center = scale([ view.size.width / 2, view.size.height / 2 ]);

Math.seedrandom('Stuart Mark Robinson');

var slices = 4 + Math.floor( Math.random() * 4 ),
    color_bands = [];

for (var i = 0; i < slices; i++) { 
  color_bands[i] = {
    hue: Math.random() * 360, // saturation: Math.random()
  };
}

for (var i = n_rings, l = 0; i > l; i--) {
  var black = i % 2,
      pos = weakness * black + Math.sqrt(i) * n_rings/1,
      c = new Path.Circle( center, scale( pos ) );

  var col = color_bands[ Math.floor( (i-1)/(n_rings/slices) ) ];
  c.fillColor = '#f0f';
//  c.fillColor.hue = color_offset - (i * color_range);
  _.extend(c.fillColor, col);

  c.fillColor.brightness = (i / 12);
  c.scale( x_scale, y_scale );
//  c.position.x += scale( Math.random() * 3); c.position.y += scale( Math.random() * 3);
  if (black) {
    c.fillColor = 'black';
  }
}
