paper.project.layers[0].removeChildren();
var rand = Math.seedrandom('Anne Paque.');

// utils?
function band(i, total, bands) { return Math.floor( (i-1)/(total/bands) ); }

var center = scale([ view.size.width / 2, view.size.height / 2 ]),
    slices = 4 + Math.floor( rand() * 4 ),
    color_bands = [];

for (var i = 0; i < slices; i++) { 
  color_bands[i] = {
    hue: rand() * 360 // saturation: Math.random()
  };
}

for (var i = n_rings, l = 0; i > l; i--) {
  var black   = i % 2,
      pos     = weakness * black + Math.sqrt(i) * n_rings/1,
      c       = new Path.Circle( center, scale( pos ) ),
      band_n  = band(i, n_rings, slices),
      col     = color_bands[ band_n ];
  c.fillColor = '#f0f';
  _.extend(c.fillColor, col);
  //c.fillColor.hue += ( band_n % 1);
  c.fillColor.brightness = (i / 12);
  c.scale( x_scale, y_scale );
  if (black) c.fillColor = 'black';
}
