paper.project.layers[0].removeChildren();
var p = new Path(),
    c_x = view.size.width / 2,
    c_y = view.size.height / 2,
    r = Math.seedrandom('Robinson'), r2 = Math.seedrandom('Stu'), sk = Skewer({ 0.9: 0.2 });

_.extend( p, { fullySelected: 0,  strokeColor: '#ddd', strokeWidth: scale(thickness), closed: 0 });

var init_x = c_x, init_y = c_y;

for (var i = 1; i < 28; i++) {
  var new_p = p.clone();
  init_x =  c_x - i * growth;
  init_y =  i * growth;
  edges = {
    b: init_y + growth * i * 2,   t: 100, // init_y - growth - thickness/2.2, 
    l: init_x ,   r: init_x + growth * i * 2
  };

  new_p.segments[0].setPoint( scale([   init_x, 100  ]) );
  new_p.add( scale([   edges.r,  100   ]) );
  new_p.add( scale([   edges.r,  edges.b  ]) );
  new_p.add( scale([   edges.l,  edges.b  ]) );
  new_p.add( scale([   edges.l,  edges.t  ]) );
}
