paper.project.layers[0].removeChildren();
var p = new Path(),
    center = scale([ view.size.width / 2, view.size.height / 2 ]),
    r = Math.seedrandom('Stuart Robinson'),
    r2 = Math.seedrandom('Stuart'),
    sk = Skewer({ 0.9: 0.2 });

_.extend( p, { fullySelected: 0,  strokeColor: '#fff',  
               fillColor: '#000',  strokeWidth: scale(thick) });

for (var i = 2; i < lines_n; i++) { 
  window.new_p = p.clone(); 

  new_p.segments[0].setPoint( scale([ 10, i*spacing]) );

  for (var j = 3; j < 100; j++) {
    var x = 5 * j + r2()*1,
        gauge = (j < 25 || j > 75) ? 0.1 : 1,
        y = i * spacing - sk( r() ) * line_height * gauge;
    new_p.add( scale([x, y]) );
  }
  new_p.add( scale([ 500, i*spacing ]) );
  new_p.smooth()
}
