paper.project.layers[0].removeChildren();
var cols = 16,  size = 22.,  off = [60, 40], space = [ 32.5, 1.193 ];
for (var i = 0, l = 624;  i < l;  i++) {
  var even      = i % 2,
      row_n     = int(i / cols),
      col_n     = i % cols,
      odd_row   = mod( i % (2 * cols), cols ),
      third_row = mod( i % (3 * cols), cols );

  var x = off[0] + space[0] * (i % cols),
      y = off[1] + space[1]  * mod(i, cols);

  var boo = (odd_row && ! even) || (!odd_row && even); 
  b = new Path.RegularPolygon( [ x, y ], 3, size );
  var rotation = !odd_row ? (30 + i * 60) : (90+ i * 60);  

  b.rotate( rotation, new Point( x, y ) );
  b.position.x -= boo * 10.7;

  b.fillColor = '#00f';
  b.fillColor.hue = i / 10 + 190;
  b.fillColor.alpha = 100 - i / 200;
  if (row_n == 37 || col_n == 0 || col_n == cols-1 || row_n == 38)  b.fillColor = '#000';
}
