functions.paperjs:
if (v.count > 1000) {
  paper.project.layers[0].removeChildren();
  v.count = 0;
}

var letters = {
  a: [  [ 0, 64], [32,  0], [64, 64], [48, 32], [16, 32] ],
  b: [  [16, 64], [16, 0], [64, 16], [16, 32], [64, 48], [16, 64]  ],
  c: [  [64,  0], [ 0, 0], [ 0, 64], [64, 64]]
};

var letter = new Path( letters.c ); // TODO use Point to fix scaling
_.extend( letter, { fullySelected: 0 } );

letter.scale(4);
letter.position = new Point([300, 300]);


for (i=0; i<100; i++) {
  var p = new Point([ Math.random()* 600, Math.random()*600 ]);
  var nearest = letter.getNearestLocation(p);

  var size = Math.min(60/nearest.distance, 10);
  if (size > 1) {
    v.count++;
    var dot = new Path.Circle(p, size);
  //_.extend( dot, { fullySelected: 1 } );
    dot.fillColor = '#fff';
  }
}
