functions.initial:
paper.project.layers[0].removeChildren();

v.p = new Path();
_.extend( v.p, { fullySelected: 0,  strokeColor: '#eee', //fillColor: '#000' 
});

v.r = Math.seedrandom("ooo");

v.lines = new Array(lines_n);

for (var i = 0; i < lines_n; i++) {
  var new_p = v.p.clone();
  v.lines[i] = new_p;
}

functions.paperjs:
time = ev.count/pace;

var n_seg = 60,
    x_seg = (view.size.width - 10) / n_seg;

for (var i = 0; i < lines_n; i++) {

  var line = v.lines[i],
      winding = 2.2 - PerlinNoise.noise(1, time/10, i*1) / 2,
      y_off = 0, //spacing * ev.count,
      y = y_off + i * spacing;

  if (line.segments.length == 1) {
    line.segments[0].setPoint( adjust([ 10, y ]) );
    for (var j = 0; j < n_seg; j++) {
      line.add(adjust([x, y]));
    }
    line.add( adjust([x + x_seg, y]) );
  }

  line.strokeWidth = adjust(thick);

  for (var j = 0; j < n_seg; j++) {
    var x =  x_seg * j, //+ r2()*0,
        bump,
        guide = n_seg - Math.abs(n_seg/winding - j) * 3.6;

    guide =  Math.pow( clamp(guide, 2, 800), 1.1 );

    bump = 2 * Math.pow(PerlinNoise.noise(x/x_noise, y/y_noise, time), 2);

    line.segments[j].setPoint( adjust([ x, y - 2 - bump * guide ]) );
    //line.add(adjust([x, y - 2 - bump * guide]));
  }

  line.segments[n_seg].setPoint( adjust([ x + x_seg, y  ]) );

}
