paper.project.layers[0].removeChildren();
var p = new Path(),
    xy = adjust([ view.size.width, view.size.height ]),
    r = Math.seedrandom('Robinson'), r2 = Math.seedrandom('Stu'),
    sk = Skewer({ 0.9: 0.2 });

_.extend( p, { fullySelected: 0,  strokeColor: '#ccc', fillColor: '#000', 
                strokeWidth: adjust(thick) });

var n_seg = 100,
    x_seg = (view.size.width - 10) / n_seg;

for (var i = 5; i < lines_n; i++) {
  var new_p = p.clone();
  new_p.segments[0].setPoint( adjust([ 10, i*spacing]) );

  for (var j = 3; j < n_seg; j++) {
    var x = x_seg * j, //+ r2()*0,
        gauge = 10, //(j < 25 || j > 75) ? 3 : 20,
        y = i * spacing;

    gauge = (n_seg - Math.abs(n_seg/2 - j) - 50) * 2;
    //y -= gauge;
    y -= -1.5 + (PerlinNoise.noise(x/40, y/50, time)) * gauge;

    new_p.add( adjust([x, y]) );
  }
  new_p.add( adjust([x + x_seg, i*spacing]) );

  new_p.smooth();
}
