functions.initial:
// v is passed to the main loop as with(v)
v.points = 399;

v.path = new Path();
v.path.closed = false;
v.path.strokeColor = '#ff0';
v.path.fullySelected = false;
v.path.strokeWidth = 0;

v.initializePath = function (points) {
  v.center = view.center;
  v.width  = view.size.width;
  v.height = view.size.height / 2;

  v.path.segments = [];
  for (var i = 0; i < points; i++) {
    var point = new Point( v.width / points * i, view.center.y );
    v.path.add(point);
  }
};

v.initializePath( v.points );

functions.paperjs:
v.path.strokeWidth = adjust( thick );
v.path.strokeColor = '#888';

for (var i = 0; i < v.points; i++) {
  var b = i % 2,
      c = Math.floor(i / 2)   * vert,
      d = Math.sin(i * 10000) * wiggle,
      x = adjust( 10 + b * (v.width - 20) ),
      y = adjust( 10 + d + c );

  if (y > 740) {
    x = v.path.segments[i - 1].point.x;
    y = v.path.segments[i - 1].point.y  + i;
  }

  v.path.segments[i].point.x = x;
  v.path.segments[i].point.y = y;  
}
