// ======== extending Leaflet =========

L.LatLng.prototype.getAllTiles = function() {
  var ll = this;
  return _.range(1, 20).map(function(n) {
    return L.llz_to_tile(ll.lat, ll.lng, n)
  });
};

L.llz_to_tile = function( lat, lng, zoom ) {
  function lat2tile(lat,zoom)  { return Math.floor((1-Math.log(Math.tan(lat*Math.PI/180) + 1/Math.cos(lat*Math.PI/180))/Math.PI)/2 *Math.pow(2,zoom)); }
  function long2tile(lon,zoom) { return Math.floor((lon+180)/360*Math.pow(2,zoom)); }

  return [ zoom, long2tile(lng, zoom), lat2tile(lat, zoom) ];
}

L.tile_to_llbounds = function(zz, xx, yy) {
  function tile2long(x,z) { return (x/Math.pow(2,z)*360-180); }
  function tile2lat(y,z) {
    var n=Math.PI-2*Math.PI*y/Math.pow(2,z);
    return (180/Math.PI*Math.atan(0.5*(Math.exp(n)-Math.exp(-n))));
  }

  return new L.LatLngBounds(
    new L.LatLng( tile2lat(yy, zz),   tile2long(xx, zz) ),
    new L.LatLng( tile2lat(yy+1, zz), tile2long(xx+1, zz) )
  );
};


L.Map.prototype.latLngToContainerPoint = function( ll ) {
  return this.layerPointToContainerPoint( this.latLngToLayerPoint( ll ) );
};

L.Map.prototype.getOSMLockedBounds = function() { // square ATM but should be quadrangle
  var bounds   = this.getPixelBounds(),
      size = Math.max( bounds.max.x - bounds.min.x, bounds.max.y - bounds.min.y );
      locked = {
        x1: Math.floor( bounds.min.x/v.tileSize ) * v.tileSize,
        y1: Math.floor( bounds.min.y/v.tileSize ) * v.tileSize,
        x2: Math.ceil( bounds.max.x/v.tileSize ) * v.tileSize,
        y2: Math.ceil( bounds.max.y/v.tileSize ) * v.tileSize
      };
  return locked;
};

L.Map.prototype.getVisibleTiles = function () {
  var locked = this.getOSMLockedBounds();
  var cols = Math.ceil((locked.x2 - locked.x1) / v.tileSize),
      rows = Math.ceil((locked.y2 - locked.y1) / v.tileSize),
      tiles = [];

  for (i=0; i<cols; i++) {
    for (j=0; j<rows; j++) {
      var x  = locked.x1 + (v.tileSize * i), 
          y  = locked.y1 + (v.tileSize * j),
          nw = this.unproject( new L.Point(x, y) ),
          se = this.unproject( new L.Point(x + v.tileSize, y + v.tileSize) );
      tiles.push( new L.LatLngBounds(nw, se) );
    }
  }
  return tiles;
};



