Scan.start = function() {
  var	center = v.map.getBounds().getCenter(),
      initial_level = v.map.getZoom();

  Scan.queue = _( v.map.getVisibleTiles() ).chain().map( function(tile) {
    var quad = new v.Quad( tile, initial_level ); // hacky
    return v.QuadTree.get( quad.key() );
  }).sortBy( function(quad) { // sort by closest to the center
    return quad.bounds.getCenter().distanceTo( center );
  }).value();

  nextScan();

  function nextScan() { // called when a scan is complete, inits the next scan
    v.layergroup.clearLayers();
    v.dotTileQueue.throttledRedraw();

    if (Scan.paused) { setTimeout(nextScan, 10000); return; }

    var up_next;
    while (up_next = Scan.queue.shift()) {
      if ((up_next.z < (initial_level + Scan.recurse_n)) && // max amount to recurse
          (up_next.z <= Scan.max_level) &&                  // max level to ever scan
          true )
      {
        highlightBounds( up_next.bounds );
        up_next.scan( nextScan );
        break; // leave the while()
      }
    }
  }
};


