v.Venue.prototype.init = function(){
  if (v.venues[this.id]) { // safety
    // this = v.venues[this.id];
  } else { // add venue if it's new
    v.venues[this.id] = this;
  }
  this.populate();
}

v.Venue.prototype.populate = function(){
  this.score = this.calcScore();
  this.ll    = new L.LatLng( this.location.lat, this.location.lng );
  this.color = this.calcColor();
  this.category = {  name: '', id: '' };
  if (this.categories[0]) {
    this.category = Categories.flat[ this.categories[0].id ];
  }
  this.dotSize = this.calcDotSize();
  this.hide = //this.score < highpass ||
              !this.category.id || 
              this.category.root.name in Categories.noShowRoots ||
              this.category.name in Categories.noShow;
};

v.Venue.prototype.insertToQuads = function(){
  var that = this;
  if (Scan.load_crap || this.displayWorthy()) {
    var tiles = that.ll.getAllTiles();
    _.each(tiles, function(tile){
      var quad = v.QuadTree.get( tile.join('/') );
      if (! quad.venues[ that.id ]) {
        quad.venues[ that.id ] = that;
      }
    });
  }
};

v.Venue.prototype.minimal = function(){
  var newVenue = _.pick(this, 'id', 'name', 'stats');
  if (this.categories[0]) newVenue.categories = [{ id: this.categories[0].id }];
  newVenue.location = { 
    lat: this.location.lat, lng: this.location.lng, postalCode: this.location.postalCode
  };
  return newVenue;
};


v.Venue.prototype.calcDotSize = function() {
  return v.dotSize.scale * clamp(this.score, v.dotSize.min, v.dotSize.max );
};

v.Venue.prototype.displayWorthy = function(){
  return (this.score > highpass && !this.hide);
};

v.Venue.prototype.getColor = function(fullStrength, offset) {
  var color = this.calcColor();

  if (fullStrength) {
    color = color.value(1).alpha(1);
  } 

  var hex = '0123456789abcdef';
  if (offset) {
    color = color.value( this.score/15 ).hue( 
      (hex.indexOf(this.id[5]) - hex.indexOf(this.id[4]) ) / 160, true 
    );
  }

  return color;
};

v.Venue.prototype.clusterPosition = function(){
  return _.indexOf(cluster.venues, this);
};



v.Venue.prototype.calcColor = function() {
  if (! this.category) return v.Venue.categoryColors.trans;

  return v.Venue.categoryColors[ this.category.root.name ];
};

v.Venue.prototype.calcScore = function() {
  return 11 * Math.pow(this.stats.usersCount/100 * this.stats.checkinsCount/20, 0.12);
};



// ======= QuadTree ========

v.QuadTree.get = function(id) { // make if none
  var quad = v.QuadTree.all[id],
      tile = id.split('/');
  if (! quad) {
    quad = new v.Quad( L.tile_to_llbounds(tile[0]-0, tile[1]-0, tile[2]-0), tile[0]-0 );
    v.QuadTree.add( quad );
  }
  return quad;
};

v.QuadTree.add = function(quad) { // don't overwrite
  var existing = v.QuadTree.all[ quad.key() ];
  if (! existing) {
    v.QuadTree.all[ quad.key() ] = quad;
  }
};

v.QuadTree.level = function(z) {
  return _.filter(v.QuadTree.all, function(q){ return q.z == z; });
};


v.QuadTree.visualise = function() {
  clearHighlights();
  _.each(_.range(15, 20), function(n){
    _( v.QuadTree.level(n) ).each(function(q){
      var _venues = _(v.venues);

      var color = { r:0, g: 0, b:0, white: 0 };
      var level_adjust = Math.pow( n - 15, 1);

      _venues.each(function(venue) {
        color[ venue.calcColor() ] += venue.score;
      });
      var white = color.white * level_adjust / 10;
      if (_venues.size() > 0) {
        q.highlight({ color: 'rgb(' + int(white + color.r *2* level_adjust) + ',' +
                                      int(white + color.g *2* level_adjust) + ',' + 
                                      int(white + color.b * level_adjust) + ')' });
      }
    });
  }); 
};
