v.dotTiles.drawTile = function() { // add to queue
  if (! v.no_dots) {
    v.dotTileQueue.renderQueue.push({
      canvas: arguments[0], tilePoint: arguments[1], zoom: arguments[2]
    });
  }
};

v.dotTileQueue = {
  lazies: [],
  cancelRender: function(){
//log('cancelRender() ' + v.lazies.length + ' lzs & ' + v.renderQueue.tasks.length + ' rndrs')
    _(v.dotTileQueue.lazies).each(function(lazy) {
      if (lazy) lazy(); // cancel
    });
    v.dotTileQueue.lazies = [];
    v.dotTileQueue.tasks  = [];
    v.dotTileQueue.renderQueue = async.queue( v.dotTileQueue.drawTile, 1 );
  },
  drawTile: function(args, callback) {
    var canvas = args.canvas,  
        tilePoint = args.tilePoint,  
        zoom = args.zoom,
        key    = zoom + '/' + tilePoint.x + '/' + tilePoint.y,
        ctx    = canvas.getContext('2d'),
        quad   = v.QuadTree.get( key ),
        venues = // (v.map.getZoom() < 14) ? quad.venues :
                 quad.getNearbyVenues();

    ctx.globalCompositeOperation = "lighter";

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    if (ui.BrightSwitch.mode != 'c') {
      ctx.fillStyle = "black";
      ctx.fillRect(0, 0, canvas.width, canvas.height);
    }
    var zoomOffset = zoomAdjust();
    var options = { complete: callback,  partSize: 40,  breathTime: 0 };
    var lazy = $.lazyEach(venues, function(n, venue){ venue = this; 
      var pos   = tilePos(venue.location, tilePoint);
        var color = ui.BrightSwitch.mode != 'c' ? venue.getColor().cssa() : 'rgba(0,0,0,1)';
        ctx.prop({ fillStyle: color })
           .circle( pos.x, pos.y, venue.dotSize * zoomOffset ).fill();
    }, options);

    v.dotTileQueue.lazies.push( lazy ); // we might have to cancel them shortly
  }
};

v.dotTileQueue.throttledRedraw = _.debounce(function(){
  v.dotTileQueue.cancelRender();
  v.dotTiles.redraw();
}, v.redraw_delay, true);

v.dotTileQueue.renderQueue = async.queue( v.dotTileQueue.drawTile, 1 );



