v.clusterTiles.drawTile = function(canvas, tilePoint, zoom) { 
  var key = zoom + '/' + tilePoint.x + '/' + tilePoint.y,
      ctx = canvas.getContext('2d');

  ctx.clearRect(0, 0, canvas.width, canvas.height);

  if (cluster.active()) {
    var center = tilePos( cluster.center(), tilePoint );

    // search / 5 minute circles
    ctx.prop({ strokeStyle: '#ddd', fillStyle: 'rgba(0,0,0,0.3)', lineWidth: 1 })
         .circle( center.x, center.y, zoomAdjust() *  64 ).fill();

    ctx.beginPath().prop({
      lineCap: 'round', lineWidth: 25 * zoomAdjust(), fillStyle: '#fff', 
      strokeStyle: 'rgba(0,0,0,0.5)'
    }).moveTo( center.x, center.y ).fill();

    // TODO: performance
    // var tileBounds = new LngLatBounds();
    // if (tileBounds.intersects( v.cluster.bounds )) {
    // if (v.cluster.length > 2) path.closed = true;

    var n = 0;
    _(cluster.venues).each(function(venue) { // async?
      var pre = cluster.venues[n-1] || _(cluster.venues).last(),
          pre_xy = tilePos(pre.ll, tilePoint),
          xy     = tilePos(venue.ll, tilePoint);
      ctx.lineTo( xy.x, xy.y ).moveTo( center.x, center.y ).fill();
      n++;
    });
    ctx.stroke().closePath();

    _(cluster.venues).each(function(venue) { // async?
      var pos   = tilePos(venue.ll, tilePoint),
          color = venue.getColor(true, true);
      color = (color.hex() == '#ffffff') ? color.value(0.5) : color;
      ctx.prop({ fillStyle: color.cssa() })
         .circle( pos.x, pos.y, zoomAdjust() * 3 ).fill();
    });
  }
};


