  var tile = L.llz_to_tile( ll.lat, ll.lng, zoom );
  return v.QuadTree.get( tile.join('/') ).getNearbyVenues();
};

window.cluster.getClosestVenues = function(ll, zoom) {
  return _( cluster.getCloseVenues(ll, zoom) ).sortBy(function(venue){
    return venue.ll.distanceTo( ll );
