};


window.cluster.gatherAll = function (ll) {
  var localVenues = cluster.getClosestVenues(ll, 17);
  cluster.closest = localVenues[0];

  if (!cluster.closest || ll.distanceTo(cluster.closest.ll) > zoomAdjust()*500) { // TODO include zoom level
    cluster.clear();
    return;
  }
  v.dotTiles.setOpacity( 0.65 );
  cluster.venues = localVenues;
  v.clusterTiles.redraw();
}


window.cluster.gather = function (ll) {
  var localVenues = cluster.getClosestVenues(ll);
  cluster.closest = localVenues[0];

  if (!cluster.closest || ll.distanceTo(cluster.closest.ll) > zoomAdjust()*500) { // TODO include zoom level
    cluster.clear();
    return;
  }

  v.dotTiles.setOpacity( 0.65 );

  cluster.venues = _(localVenues).filter(function(venue){
    return venue.ll.distanceTo( ll ) < v.cluster_range;
  });

  var n_within = cluster.venues.length;

  cluster.venues = _(cluster.venues).chain().slice(0, 12)
                      .sortBy(function(venue) { return -venue.ll.lat; }).value();
  var others_n = n_within - cluster.venues.length;
  var postcodes = _(cluster.venues).chain().map(function(venue){ return venue.location.postalCode })
                    .compact().map(function(pc){ return pc.split(' ')[0].toUpperCase() })
                    .compact().uniq().value();

  var bounds   = cluster.bounds(),
      distance = bounds.getNorthWest().distanceTo( bounds.getSouthEast() ),
      color_count = {};

  var str = '<div class="venue"><b style="background-color: <%= color %>"></b> ' +
              '<a target="_new" href="http://foursquare.com/v/<%= id %>"><%= name %></a> ' +
              '<em><%= category.name %></em></div> ';

  var more_str = others_n > 0 ? 
        '<div class="others">+ ' + others_n  + ' places within ' + v.cluster_range/50 + ' minutes</div>' : ''

  var text = _(cluster.venues).map(function(venue) {
    var color = venue.getColor(true, true);
    color = (color.hex() == '#ffffff') ? color.value(0.5) : color;

    if (color_count[ color.hex() ] >= 0) {
      color_count[ color.hex() ] += 1;
    } else {
      color_count[ color.hex() ] = 0;
    }

    venue.color = color.alpha(1).cssa(); //.hue((color_count[ color.hex() ] / 30), true)
    return _.template(str)(venue);
  }).join(' ') + more_str + '<div class="postcodes">' + postcodes[0] + "</div>";

  popup( new L.LatLng( cluster.venues[0].ll.lat, cluster.venues[0].ll.lng ), text );

  v.clusterTiles.redraw();
};


