v.Quad.offZero = function(num) { // hack for foursquare API
  return num === 0 ? '0.00000000001' : num + '';
};

v.Quad.prototype.highlight = function(options) {
  highlightBounds(this.bounds, true, options);
};


v.Quad.prototype.scan = function(callback) {
  var that = this;
  var onScanned = function(results) {
    var quad = v.QuadTree.get( that.key() );

    if (results) { // if called after an actual AJAX request
      quad.fetched_at = Date.now();
      quad.venue_count = results.response.venues.length;
    }
    if (! quad.barren) {
      var kids = that.divide(); //getChildrenTiles
      Scan.queue.push( kids[0], kids[1], kids[2], kids[3] );
    }
    if (callback) _.defer( callback, 0 ); // defer to protect the stack
  };
  fetchVenues( that, onScanned );
};

v.Quad.prototype.getNeighbouringTiles = function () {
  var that = this,
      directions = [  [-1, -1], [-1, +0], [-1, +1],
                      [+0, -1], /*[0,0]*/ [+0, +1],
                      [+1, -1], [+1, +0], [+1, +1]  ];

  return _.map( directions, function(dir) {
    return [ that.z,  that.x + dir[0],  that.y + dir[1] ];
  });
};

v.Quad.prototype.getNearbyVenues = function() { 
  //if (this.nearbyCache) return this.nearbyCache;
  var venues = _.clone(this.venues),
      bounds = this.bounds,
      nw = bounds.getNorthWest(),
      se = bounds.getSouthEast(),
      padding = 0.005 * zoomAdjust(),
      lat_max = nw.lat + padding, 
      lat_min = se.lat - padding,
      lng_min = nw.lng - padding,
      lng_max = se.lng + padding,
      tiles  = this.getNeighbouringTiles(), n = 0;

  _.each(tiles, function(t) {
    var quad = v.QuadTree.get( t.join('/') );

    for (var key in quad.venues) {
      var venue = quad.venues[key];
      if ((lat_min < venue.ll.lat && venue.ll.lat < lat_max) && 
          (lng_min < venue.ll.lng && venue.ll.lng < lng_max)) {
        venues[key] = venue;
      } else { n++ }
    }
  });
  return venues;
};

v.Quad.prototype.getParentTile = function() {
  var center = this.bounds.getCenter();
  return L.llz_to_tile( center.lat, center.lng, this.z - 1);
};

v.Quad.prototype.getParent = function(){
  return v.QuadTree.get( this.getParentTile().join('/') );
}

v.Quad.prototype.worthScanning = function() { 
  var parent  = v.QuadTree.get( this.getParentTile().join('/') ),
      gparent = v.QuadTree.get( parent.getParentTile().join('/') );

  var p_n  = (parent.venue_count  == undefined) ? 50 : parent.venue_count;
      gp_n = (gparent.venue_count == undefined) ? 50 : gparent.venue_count;

  return (p_n + gp_n > 95);
};


v.Quad.prototype.getChildrenTiles = function(){
  var zz = this.z + 1,  x = this.x,  y = this.y;

  return [
    [ zz, 2*x, 2*y ],    [ zz, 2*x + 1, 2*y ],
    [ zz, 2*x, 2*y + 1], [ zz, 2*x + 1, 2*y + 1 ]
  ];
};

v.Quad.prototype.tile = function() {
  var center = this.bounds.getCenter();
  return L.llz_to_tile( center.lat, center.lng, this.z );
};

v.Quad.prototype.key = function() {
  return this.tile().join('/');
};

v.Quad.prototype.done = function() { // FIXME: possible bug here!
  return (this.z < 19) && (
    this.fetched_at || _(this.getChildrenTiles()).all( function(tile_arr) {
      return v.QuadTree.get( tile_arr.join('/') ).done();
    })
  );
};

v.Quad.prototype.divide = function() {
  var bounds = this.bounds,
      midLL = function(a, b) { return (new L.LatLngBounds(a, b)).getCenter(); },  
      sw = bounds.getSouthWest(), nw = bounds.getNorthWest(),
      se = bounds.getSouthEast(), ne = bounds.getNorthEast(),
      n  = midLL(nw, ne),         s  = midLL(sw, se),
      w  = midLL(nw, sw),         e  = midLL(ne, se),    x = midLL(n,  s);

  return [ // sw, ne
    new v.Quad( new L.LatLngBounds( w, n ), this.z + 1 ),
    new v.Quad( new L.LatLngBounds( x, ne), this.z + 1 ),
    new v.Quad( new L.LatLngBounds(sw, x ), this.z + 1 ),
    new v.Quad( new L.LatLngBounds( s, e ), this.z + 1 )
  ];
};


