functions.instant:
function long2tile(lon,zoom) { return Math.floor((lon+180)/360*Math.pow(2,zoom)); }
function lat2tile(lat,zoom)  { return Math.floor((1-Math.log(Math.tan(lat*Math.PI/180) + 1/Math.cos(lat*Math.PI/180))/Math.PI)/2 *Math.pow(2,zoom)); }
function tile2long(x,z) { return (x/Math.pow(2,z)*360-180); }
function tile2lat(y,z) {
  var n=Math.PI-2*Math.PI*y/Math.pow(2,z);
  return (180/Math.PI*Math.atan(0.5*(Math.exp(n)-Math.exp(-n))));
}

window.tilesForLocation = function(ll) {
  var tiles = [];
  for (var i=1; i<19; i++) {
    tiles.push([ i, long2tile(ll.lng, i), lat2tile(ll.lat, i) ]);
  }
  return tiles;
}

window.zoomAdjust = function() {
   return Math.pow(2, v.map.getZoom()) / 26214.4
}

window.refetch = function(){
  window.currentQuery && window.currentQuery.abort && window.currentQuery.abort();
  window.queue = [];
  window.startScan( v.map );
} 

window.startScan = function(map) {
  var	center = v.map.getBounds().getCenter();
  v.scanLevel = v.map.getZoom(); 

  v.queue = _( v.map.getVisibleTiles() ).map(function(tile) {
    return new v.Quad( tile, v.scanLevel );
  });

  _( v.queue ).sort(function (a, b) { // sort by closest to the center
    return a.bounds.getCenter().distanceTo(center) - b.bounds.getCenter().distanceTo(center)
  });

  nextScan();

  function nextScan() {
    v.scans = [];
    v.canvasTiles.redraw();

    if (v.pauseScan) { setTimeout(nextScan, 10000); return; }

    var up_next;
    while (up_next = v.queue.shift()) {
      if (v.scanLevel + 1 > up_next.level && up_next.diagonal > v.smallest_search_diagonal) {
        v.scans[0] = up_next;
        up_next.scan( nextScan );
        break;
      }
    }
  }
};

v.canvasTiles.drawTile = function(canvas, tilePoint, zoom) {
  function tilePos(ll, tilePoint) {
    var point = v.map.project( ll );
    return {  x: point.x - tilePoint.x * v.tileSize,  y: point.y - tilePoint.y * v.tileSize  }
  }

  var key = zoom + '/' + tilePoint.x + '/' + tilePoint.y;

  var ctx = canvas.getContext('2d');
  ctx.clearRect(0, 0, canvas.width, canvas.height);
  ctx.globalAlpha = 1;

  if (cluster.active()) {
    var center = tilePos( cluster.center(), tilePoint );
    ctx.beginPath().prop({
      lineCap: 'round', lineWidth: 50, fillStyle: '#fff', strokeStyle: '#fff'
    }).moveTo( center.x, center.y ).fill();

    // TODO: performance
    // var tileBounds = new LngLatBounds();
    // if (tileBounds.intersects( v.cluster.bounds )) {
    // if (v.cluster.length > 2) path.closed = true;

    var n = 0;
    _(cluster.venues).each(function(venue) {
      var pre = cluster.venues[n-1] || _(cluster.venues).last(),
          pre_xy = tilePos(pre.ll, tilePoint),
          xy     = tilePos(venue.ll, tilePoint);
      ctx.lineTo( xy.x, xy.y ).moveTo( center.x, center.y ).fill();
      n++;
    });
    ctx.stroke().closePath();
  }
  ctx.globalAlpha = 1;


  if (!v.QuadTree.all[key]) return; // kids?

  var i = 0;
  _(v.QuadTree.all[key].getNearbyVenues()).each(function(venue){
    var score = v.calcScore( venue ),
        zoom = zoomAdjust(),
        size = Math.max( 3,
          Math.min( Math.pow(score, power) * zoom, 14)
        );

    if (! venue.hide && score > highpass) { //var x = 11, y = 30 + 14*i++;
      var clustered = cluster.active() && cluster.contains( venue ),
          pos   = tilePos(venue.location, tilePoint),
          color = clustered ? venue.color : (cluster.active() ? '#555' : '#000');

      ctx.prop({ fillStyle: color }).circle( pos.x, pos.y, clustered ? 8 : size ).fill();
    }
  });
};

window.cluster.bounds = function() { 
  return new L.LatLngBounds( _.map(cluster.venues, function(venue) { return venue.ll }) )
