window.cluster.center = function() {
  var count = cluster.venues.length;
  var total = _(cluster.venues).reduce(function(tally, venue) {
    if (tally) {
      return { lat: (tally.lat + venue.ll.lat),  lng: (tally.lng + venue.ll.lng) }
    } else {
      return venue.ll;
    }
  }, null);

  return new L.LatLng( total.lat/count, total.lng/count );
};

