  var zoom = 15,
      x    = long2tile(ll.lng, zoom),
      y    = lat2tile(ll.lat, zoom),
      tile = zoom + '/' + x + '/' + y;

  return v.QuadTree.all[ tile ].getNearbyVenues();
};

window.cluster.getClosestVenues = function(ll) {
  return _( cluster.getCloseVenues(ll) ).sortBy(function(venue){
    return venue.ll.distanceTo( ll );
  });
}


window.cluster.gather = function (ll) {
  var localVenues = cluster.getClosestVenues(ll),
      closest     = localVenues[0];

  if (ll.distanceTo(closest.ll > 100)) { // TODO include zoom level
    cluster.venues = [];
    v.canvasTiles.redraw();
    return;
  }

  cluster.venues = _(localVenues).filter(function(venue){
    return venue.ll.distanceTo( ll ) < v.cluster_range;
  });

  // limit to 12
  cluster.venues = cluster.venues.slice(0, 12).sort(function (a, b) {
    return b.ll.lat - a.ll.lat
  });

  function cat(venue) {
    if (venue.categories.length && venue.categories[0] && venue.categories[0].name) {
      return venue.categories[0].name;
    }
  }

  var text =  _.map(cluster.venues, function(venue) {
    var str = 
      '<b style="-webkit-border-radius: 8px; padding: 0 6px;' + 
         'background-color: <%= color %>">&nbsp;</b> ' +
        '<a target="_new" href="http://foursquare.com/v/<%= id %>"><%= name %></a>' +
        ' <em>'+ cat(venue) + '</em><br>';
    return _.template(str)(venue);
  }).join(' ');

  v.canvasTiles.redraw();

  popup( 
    new L.LatLng( cluster.bounds().getNorthWest().lat, cluster.bounds().getCenter().lng ), 
    text 
  );
}


