// Quad class

v.Quad = function(bounds, level) {
  this.level = level;
  this.bounds   = bounds;
  this.diagonal = bounds.getNorthWest().distanceTo( bounds.getSouthEast() )
  this.venues   = {};

  var tile = this.tile();
  this.x = tile[1];
  this.y = tile[2];
  this.z = tile[0];

//this.hit_count      0-50
//this.ajax_done  boolean

  if (! v.QuadTree.all[ this.key() ]) {
    v.QuadTree.all[ this.key() ] = this;
  }
  return this;
}

v.Quad.offZero = function(num) { // hack for foursquare API
  return num === 0 ? '0.00000000001' : num + ''
}

v.Quad.prototype.getNeighbouringTiles = function () {
  var that = this;
  var directions = [
    [-1, -1], [-1, +0], [-1, +1],
    [+0, -1], /*[0,0],*/[+0, +1],
    [+1, -1], [+1, +0], [+1, +1]
  ];

  return _.map( directions, function(dir) {
    return [ that.z, that.x + dir[0], that.y + dir[1] ].join('/');
  });
}

v.Quad.prototype.getNearbyVenues = function() { 
  var venues = _.clone(this.venues),
      tiles  = this.getNeighbouringTiles();

  _.each(tiles, function(t) {
    if (v.QuadTree.all[t]) {
      _.extend( venues, v.QuadTree.all[t].venues );
    } 
  });

  venues = _.filter(venues, function(venue){
    var score = v.calcScore( venue );
    return !venue.hide && (score > highpass);
  });

  return venues;
}

v.Quad.prototype.getParentTiles = function() {
  return this.tile(1);
}

v.Quad.prototype.getChildrenTiles = function(){
  var zz = this.z + 1,
      x = this.x, y = this.y;

  return [
    [ zz, 2*x, 2*y ],   [ zz, 2*x + 1, 2*y ],
    [ zz, 2*x, 2*y + 1], [ zz, 2*x + 1, 2*y + 1 ]
  ];
}

v.Quad.prototype.tile = function(up) { // up = how many levels to jump up
  var level = this.level - (up ? up : 0);
  var lat = this.bounds.getCenter().lat, lng = this.bounds.getCenter().lng;

  return [ level, long2tile(lng, level), lat2tile(lat, level) ];
}

v.Quad.prototype.key = function() {
  return this.tile().join('/');
}

v.Quad.prototype.done = function() { // NOT used atm, will be when search can resume
  if (this.result_count < 50) return true; // got fewer than complete results
  if (! this.ajax_done || kids.length < 4) return false;

  if (kids.length == 4) {
    return kids[0].done() && kids[1].done() && kids[2].done() && kids[3].done();
  }
}

v.Quad.prototype.divide = function() {
  var bounds = this.bounds,
      midLL = function(a, b) { return (new L.LatLngBounds(a, b)).getCenter() },  
      sw = bounds.getSouthWest(), nw = bounds.getNorthWest(),
      se = bounds.getSouthEast(), ne = bounds.getNorthEast(),
      n  = midLL(nw, ne),         s  = midLL(sw, se),
      w  = midLL(nw, sw),         e  = midLL(ne, se),    x = midLL(n,  s);

  return [ // sw, ne
    new v.Quad( new L.LatLngBounds( w, n ), this.level + 1 ),
    new v.Quad( new L.LatLngBounds( x, ne), this.level + 1 ),
    new v.Quad( new L.LatLngBounds(sw, x ), this.level + 1 ),
    new v.Quad( new L.LatLngBounds( s, e ), this.level + 1 )
  ];
};


v.Quad.prototype.scan = function(callback) {
  var that = this;
  var onScanned = function(results) {
    var venues = results.response.venues;
    that.ajax_complete = true;
    that.result_count = venues.length;

    _.each(venues, function(venue){
      // FIXME dupe hack, fix datamodel please!
      venue.ll = new L.LatLng( venue.location.lat, venue.location.lng );
      venue.color = get_random_color();

      var tiles = tilesForLocation( venue.location );
      _.each(tiles, function(tile){
        var key = tile.join('/'),
            other_quad = v.QuadTree.all[ tile.join('/') ];
        if ( other_quad ) {
          other_quad.venues[ venue.id ] = venue;
        } else { 
          var x = tile[1],  y = tile[2],  z = tile[0];
          var new_quad = new v.Quad(
            new L.LatLngBounds(
              new L.LatLng( tile2lat(y, z),     tile2long(x, z) ),
              new L.LatLng( tile2lat(y + 1, z), tile2long(x + 1, z) )
            ), z);
          new_quad.venues[ venue.id ] = venue;
        }
      })
    });

    if (venues.length == 50) { // squeeze out some kids
      var kids = that.divide();
      v.queue.push( kids[0], kids[1], kids[2], kids[3] );
    }
    callback && callback();
  };
  fetchVenues( that, onScanned );
};



