functions.paperjs:
paper.project.layers[0].removeChildren();

var center = [view.size.width / 2, view.size.height / 2];
var r = Math.seedrandom(randomiser),
    r2 = Math.seedrandom(randomiser * randomiser);
var original = new paper.Path(),
    x = center[0],
    y = center[1] - diameter * 2;

original.add( adjust([ x, center[1] - 0 ]) );
original.add( adjust([ x - thick/2, y ]) );
original.add( adjust([ x + thick/2, y ]) );
original.closed = true;
original.fillColor = '#fff020';

for (var i = 0; i < number; i++) {
  var c = original.clone(),
      rand = r(),
      jiggle = rand * messiness;

  c.segments[2].point.x += adjust( thick / number );
  c.segments[1].point.x -= adjust( thick / number );
  var myr = r2();
  c.segments[1].point.y +=  adjust(-10 + (myr * 180));
  c.segments[2].point.y +=  adjust(-10 + (myr * 180));

  c.segments[2].point.x += adjust( Math.abs( Math.sin(i*60) ) );
  c.segments[0].point.y -= adjust(jiggle * 28);
  //c.segments[2].point.x += jiggle * 0.2;
  c.rotate(i * 359.5/number, adjust(center));
  //c.add( scale([ center[0] + Math.sin(i) * diameter, center[1] + Math.cos(i) * diameter ]) );
}
