paper.project.layers[0].removeChildren();
var p    = new Path(),  hack_y,
    size = view.getSize();

_.extend( p, { fullySelected: 0,  strokeColor: '#333',  strokeWidth: adjust(thick) });

var land_ahoy = _.once(function (ry) { p.add( new Point( -1, adjust(ry) ) ) });

for (var x = -0.999, step = 1/steps; x < 0.99; x += step) {   if (step <= 0) return;
  var y = Math.pow( 4.0 - x * x, 0.1 ) * ( Math.sqrt(Math.cos( 1.7*x )) * Math.cos( density*x ) + Math.sqrt(Math.abs(x) ) ),
      real_x = size.width/2  + size.width/x_scale  * x,
      real_y = size.height/2 - size.height/y_scale * y;

  y && land_ahoy(real_y) | (hack_y = real_y);
  if (y) {
    p.add( new Point( adjust(real_x), adjust(real_y) ) )
  }
}
p.add( new Point( adjust(size.width), adjust(hack_y) ) );
