functions.paperjs:
paper.project.layers[0].removeChildren();
window.text = new PointText(new Point( scale(250), scale(300) ));
text.justification = 'center';  text.fillColor = '#ff00ff';
text.fontSize = scale(150); text.font = 'Futura';
text.content = 'V';

var count = 40,
    rot   = 360 / count,
    color_range = 8,
    color_off   = 180,
    gray_range = .0215;

    count += 1;

for (var i = 0; i < count; i++) {
  var tmp = text.clone();
  tmp.fillColor = '#fff000';
  tmp.rotate( i * rot + 10, [ scale(270), scale(240) ] );
  if (count - 1 == i) { 
    tmp.fillColor = '#f0f0f0';
  } else {
    //tmp.fillColor.hue = 0;
    tmp.fillColor.saturation = gray_range * i;
    tmp.fillColor.hue = color_off + 360 - i * color_range;
    tmp.fillColor.brightness = gray_range * i / 2.5;
  }
}

text.visible = false;
