paper.project.layers[0].removeChildren();

v.blocks = [];
var center = [ scale(view.size.width / 2), scale(view.size.height / 2) ];

for (var i = count, l = 0; i > l; i--) {
  var black = i % 2,
      c = new Path.Circle( center, scale(1 * black + Math.sqrt(i) * count/1) );

  c.fillColor = '#11f';
  //c.fillColor.hue = color_offset - (i * color_range);
  c.fillColor.brightness = (i / 12);
  c.scale( 1, 1.4 );
  //c.position.x += Math.random();
  //c.position.y += Math.random();
  if (black) {
    c.fillColor = 'black';
  }
}
