paper.project.layers[0].removeChildren();
var rows = 8, size = 50, off = [60, 50];

for (var i = 0, l = 100;  i < l;  i++) {
  var even = i % 2;
  var odd_row = (i / l) / 4;
  var x = 74 *   (i % rows) + off[0] - (even * 25),
      y = 8  * mod(i, rows) + off[1],
      b = new Path.RegularPolygon( [ x, y ], 3, size );

  b.rotate( 30 + i * 60, new Point( x, y ) );
  b.fillColor = '#00f';
  //b.fillColor.saturation = (i / rows) % 1;
  //if (odd_row) 
  b.fillColor.brightness = odd_row;
}
