paper.project.layers[0].removeChildren();
var cols=16, size=22, off=[60, 40], space=[ 32.55, 1.193 ];
for (var i = 0, l = 624;  i < l;  i++) {
  var even      = i % 2,
      row_n     = int(i / cols),
      col_n     = i % cols,
      odd_row   = mod( i % (2 * cols), cols ),
      third_row = mod( i % (3 * cols), cols ),
      x         = off[0] + space[0] * (i % cols),
      y         = off[1] + space[1]  * mod(i, cols),
      boo       = (odd_row && ! even) || (!odd_row && even),
      rotation  = !odd_row ? (30 + i * 60) : (90+ i * 60),
      scale_out =  0,
      b = new Path.RegularPolygon( [ x, y ], 3, size  - scale_out );
  b.fillColor = '#f00';
  //if (!((i-15) % 15) || !((i-6) % 15)) {  b.remove(); continue; }
  b.rotate( rotation * 1.0032, new Point( x, y ) );
  b.position.x -= boo * 10.7;

  //b.fillColor.hue =-180 + (i / 40);
  b.fillColor.saturation = 0;
  b.fillColor.alpha =  Math.sin(i/190) * 1.0 + 0.2; // 1 - i / l;
  b.fillColor.brightness = 0.1;

//if (row_n == 0 || col_n == 0 || col_n == cols-1 || row_n==38) { b.fillColor.alpha = 0.45;}
//if (i in a2o([405,406,409,420,421,422,423,424,425,437,438,441])) { b.fillColor = '#e70'; }
}
